$("input,textarea").focus(function () {
  $(this).removeAttr("placeholder");
}),
  $(".nav-toggle").click(function () {
    $(this).toggleClass("nav-close"), $(".nav-primary").slideToggle();
  }),
  $(".dropdown").click(() => {
    var e = $(this);
    e.closest("li").find("> .dropdown-content").toggleClass("js-toggled"),
      e.toggleClass("js-toggled");
  }),
  $(".nav-primary li.menu-item-has-children > a").after(
    '<span class="sub-menu-toggle fa fa-angle-down"></span>'
  ),
  $(".notificatio-cross").click(function () {
    $(".top-header").slideToggle();
  }),
  $(".js-tab-content").slick({
    slidesToShow: 1,
    slidesToScroll: 1,
    arrows: !1,
    dots: !1,
    adaptiveHeight: !0,
    fade: !0,
    asNavFor: ".js-tab-nav",
  }),
  $(".js-tab-nav").slick({
    slidesToShow: 4,
    slidesToScroll: 1,
    asNavFor: ".js-tab-content",
    dots: !1,
    arrows: !0,
    focusOnSelect: !0,
    responsive: [
      {
        breakpoint: 1200,
        settings: { slidesToShow: 3, slidesToScroll: 1, infinite: !0 },
      },
      { breakpoint: 768, settings: { slidesToShow: 2, slidesToScroll: 1 } },
      { breakpoint: 480, settings: { slidesToShow: 1, slidesToScroll: 1 } },
    ],
  }),
  $(".js-testimonial").slick({ dots: !1 }),
  $("#mobile-number").intlTelInput(),
  $("#mobile-number2").intlTelInput(),
  $("#mobile-number3").intlTelInput(),
  $("#mobile-number4").intlTelInput(),
  $("#mobile-number5").intlTelInput(),
  $(".faq-accordion h3").eq(0).addClass("active"),
  $(".faq-item").eq(0).show(),
  $(".faq-accordion h3").click(function () {
    $(this)
      .next(".faq-item")
      .slideToggle("fast")
      .siblings(".faq-item:visible")
      .slideUp("fast"),
      $(this).toggleClass("active"),
      $(this).siblings("h3").removeClass("active");
  });
const accordionItem = document.querySelectorAll(".accordion-item");
function openTab(e, t) {
  var s, o, a;
  for (
    o = document.getElementsByClassName("tabcontent"), s = 0;
    s < o.length;
    s++
  )
    o[s].style.display = "none";
  for (
    a = document.getElementsByClassName("tablinks"), s = 0;
    s < a.length;
    s++
  )
    a[s].className = a[s].className.replace(" active", "");
  (document.getElementById(t).style.display = "block"),
    (e.currentTarget.className += " active");
}
accordionItem.forEach((e) =>
  e.addEventListener("click", () => {
    e.classList.contains("active")
      ? e.classList.remove("active")
      : (accordionItem.forEach((e) => e.classList.remove("active")),
        e.classList.add("active"));
  })
),
  $(document).ready(function () {
    $("ul.tabs li").click(function () {
      var e = $(this).attr("data-tab");
      $("ul.tabs li").removeClass("current"),
        $(".tab-content").removeClass("current"),
        $(this).addClass("current"),
        $("#" + e).addClass("current");
    });
  }),
  $(".tabcontent").hide(),
  $("#tab-1").show(),
  $("#select-box").change(function () {
    (dropdown = $("#select-box").val()),
      $(".tabcontent").hide(),
      $("#tab-" + dropdown).show();
  });
